const express = require("express");
const app = express();
const PORT = 3000;

app.get("/", (req,res)=>{
    res.send("Benvenuto nella mia api");
})

app.get("/prodotti", (req,res) => {
    const prodotti = ["Laptop","Mouse","Tastiera"];
    res.json(prodotti);
})

app.get("/contatti", (req,res) =>{
    const contatti = [
        {nome: "Mario", telefono: "12345678"},
        {nome: "Luca", telefono: "54286644"}
    ];
    res.json(contatti);
})

app.listen(PORT, () => {
    console.log(`Server attivo su http://localhost:${PORT}`);
})